test.afterAll(async ({ closePage }) => {
  await closePage();
});

async function updateMonomersLibrary(
  page: Page,
  sdfString: string,
): Promise<string | null> {
  return page.evaluate(async (cmd) => {
    try {
      await window.ketcher.updateMonomersLibrary(cmd);
      return null;
    } catch (error) {
      if (error instanceof Error) {
        return error.message;
      }

      return String(error);
    }
  }, sdfString);
}

